import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';

const ServiceDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { loading, error, service } = useSelector(state => state);

  useEffect(() => {
    dispatch({ type: 'FETCH_SERVICE_REQUEST', payload: id });
  }, [dispatch, id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error} <button onClick={() => dispatch({ type: 'FETCH_SERVICE_REQUEST', payload: id })}>Retry</button></div>;

  return (
    <div>
      <h1>Service Details</h1>
      <p>{service.name}</p>
    </div>
  );
};

export default ServiceDetails;
