import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';

const ServiceDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { loading, error, service } = useSelector(state => state);

  useEffect(() => {
    dispatch({ type: 'FETCH_SERVICE_REQUEST', payload: id });
  }, [dispatch, id]);

  if (loading) return <div style={styles.loading}>Loading...</div>;
  if (error) return (
    <div style={styles.error}>
      Error: {error} 
      <button onClick={() => dispatch({ type: 'FETCH_SERVICE_REQUEST', payload: id })} style={styles.retryButton}>Retry</button>
    </div>
  );

  return (
    <div style={styles.detailsContainer}>
      <h1 style={styles.title}>Service Details</h1>
      <p style={styles.serviceName}>{service.name}</p>
    </div>
  );
};

const styles = {
  loading: {
    textAlign: 'center',
    fontSize: '20px',
    color: '#007BFF',
  },
  error: {
    textAlign: 'center',
    fontSize: '20px',
    color: 'red',
  },
  detailsContainer: {
    padding: '20px',
    textAlign: 'center',
  },
  title: {
    fontSize: '24px',
    marginBottom: '10px',
  },
  serviceName: {
    fontSize: '20px',
    fontWeight: 'bold',
  },
  retryButton: {
    marginTop: '10px',
    padding: '10px 15px',
    backgroundColor: '#007BFF',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};

export default ServiceDetails;
